#define READY_MOTION_STATUS         0   // 준비 상태
#define SPEAK_MOTION_STATUS         1   // 수어 발화 상태
#define RAPID_MOTION_STATUS         2   // 급격 동작 상태

// Optical Flow-based Motion Detection 파라미터
